graphql.query:
mutation CreateNews{
    createNews(createNewsInput:{newsTitle:"Ba học sinh tử vong dưới hồ",categoryNewsId:"4502589f-1034-4abc-be8e-658c0f540374"}){
        status
        message
        item{
            _id
            newsTitle
            categoryNews{
                _id
                categoryName
            }
            user{
                _id
                username
                email
                displayName                
            }
        }
    }
}

query FindNewsUnauthenticated{
    findNewsUnauthenticated(keyword:"duyệt quy hoạch",categoryNewsId:"4502589f-1034-4abc-be8e-658c0f540374",page:1){
        status
        message
        list{
            _id
            newsTitle
            categoryNews{
                _id
                categoryName
            }
            user{
                _id
                username
                email
                displayName                
            }
        }
    }
}

